import { __decorate } from "tslib";
import { Component } from '@angular/core';
let AutorComponent = class AutorComponent {
    constructor(httpProvider, toastService) {
        this.httpProvider = httpProvider;
        this.toastService = toastService;
        this.itemList = [];
        this.carregando = false;
        this.form = {
            id: null,
            Nome: null
        };
    }
    ngOnInit() {
        this.getAll();
    }
    async getAll() {
        this.carregando = true;
        this.httpProvider.read()
            .subscribe(res => {
            if (res != null && res.body.data != null) {
                this.itemList = res.body.data;
                this.carregando = false;
            }
        });
    }
    removeToast(index) {
        this.toastService.remove(index);
    }
    onCreate() {
        this.toastService.add('This is a toast message.', 3000, 'success');
        console.info(this.form.Nome);
        return false;
        if (!this.form.Name) {
            //this.toastr.error("Erro", "Preencha os campos obrigatório(s)!");
            return false;
        }
        if (!this.form.id) {
            // create
            this.httpProvider.create(this.form).subscribe((response) => {
                if (response) {
                    // this.toastr.success("Dados salvos com sucesso!");
                    //this.router.navigate(["/games/list"], {});
                }
            });
        }
        else {
            //update
            this.httpProvider.update(this.form).subscribe((response) => {
                if (response) {
                    // this.toastr.success("Dados alterados com sucesso!");
                    //this.router.navigate(["/games/list"], {});
                }
            });
        }
        this.getAll();
    }
};
AutorComponent = __decorate([
    Component({
        selector: 'app-autor',
        templateUrl: './autor.component.html',
        styleUrls: ['./autor.component.css']
    })
], AutorComponent);
export { AutorComponent };
