import { __decorate } from "tslib";
import { Component } from '@angular/core';
let AutorComponent = class AutorComponent {
    constructor(httpProvider, toastService) {
        this.httpProvider = httpProvider;
        this.toastService = toastService;
        this.itemList = [];
        this.carregando = false;
        this.form = {
            id: null,
            Nome: null
        };
        this.txtAddUpdate = '';
    }
    ngOnInit() {
        this.getAll();
    }
    async getAll() {
        this.carregando = true;
        this.httpProvider.read()
            .subscribe(res => {
            if (res != null && res.body.data != null) {
                this.itemList = res.body.data;
                this.carregando = false;
            }
        });
    }
    removeToast(index) {
        this.toastService.remove(index);
    }
    selectAddUpdate(type) {
        if (type === 'add') {
            this.txtAddUpdate = 'Adicionar';
        }
        else {
            this.txtAddUpdate = 'Alterar';
        }
    }
    onCreateUpdate() {
        if (!this.form.Nome) {
            this.toastService.add('Preencha os campos obrigatório(s)', 3000, 'error');
            return false;
        }
        if (!this.form.id) {
            // create
            this.httpProvider.create(this.form).subscribe((response) => {
                if (response) {
                    this.toastService.add("Dados salvos com sucesso!", 3000, 'success');
                }
            });
        }
        else {
            //update
            this.httpProvider.update(this.form).subscribe((response) => {
                if (response) {
                    this.toastService.add("Dados alterados com sucesso!", 3000, 'success');
                }
            });
        }
        this.getAll();
        return;
    }
    onEdit(id) {
        if (id) {
            this.httpProvider.edit(id)
                .subscribe(res => {
                if (res != null && res.body.data != null) {
                    this.itemList = res.body.data;
                }
            });
        }
    }
};
AutorComponent = __decorate([
    Component({
        selector: 'app-autor',
        templateUrl: './autor.component.html',
        styleUrls: ['./autor.component.css']
    })
], AutorComponent);
