export class LivroComponent {
    constructor(httpProvider, toastService) {
        this.httpProvider = httpProvider;
        this.toastService = toastService;
        this.itemList = [];
        this.autorList = [];
        this.assuntoList = [];
        this.carregando = false;
        this.form = {
            Codl: null,
            Titulo: null,
            Editora: null,
            Edicao: null,
            AnoPublicacao: null,
            Autor_CodAu: [],
            Assunto_CodAs: 0
        };
        this.txtAddUpdate = '';
        this.idDel = 0;
    }
    ngOnInit() {
        this.getAll();
        //this.getAllAutor();
        this.getAllAssunto();
    }
    async getAll() {
        this.carregando = true;
        this.httpProvider.read()
            .subscribe(res => {
            if (res != null && res.body.data != null) {
                this.itemList = res.body.data;
                this.carregando = false;
            }
        });
    }
    // async getAllAutor() {
    //   this.carregando = true;
    //   this.httpProvider.readAutor()
    //       .subscribe(res => {
    //         if (res != null && res.body.data != null) {
    //           this.autorList = res.body.data as [];
    //           this.carregando = false;
    //         }
    //       });
    // }
    async getAllAssunto() {
        this.carregando = true;
        this.httpProvider.readAssunto()
            .subscribe(res => {
            if (res != null && res.body.data != null) {
                this.assuntoList = res.body.data;
                this.carregando = false;
            }
        });
    }
    removeToast(index) {
        this.toastService.remove(index);
    }
    selectAddUpdate(type) {
        this.form = {
            Codl: null,
            Titulo: null,
            Editora: null,
            Edicao: null,
            AnoPublicacao: null,
            Autor_CodAu: [],
            Assunto_CodAs: null
        };
        if (type === 'add') {
            this.txtAddUpdate = 'Adicionar';
        }
        else {
            this.txtAddUpdate = 'Alterar';
        }
    }
    onCreateUpdate() {
        if (!this.form.Titulo && !this.form.Editora && !this.form.Edicao && !this.form.AnoPublicacao) {
            this.toastService.add('Preencha os campos obrigatório(s)', 3000, 'error');
            return false;
        }
        this.carregando = true;
        if (!this.form.Codl) {
            // create
            this.httpProvider.create(this.form).subscribe((response) => {
                if (response) {
                    this.toastService.add("Dados salvos com sucesso!", 3000, 'success');
                    this.getAll();
                }
            });
        }
        else {
            //update
            this.httpProvider.update(this.form, this.form.Codl).subscribe((response) => {
                if (response) {
                    this.toastService.add("Dados alterados com sucesso!", 3000, 'success');
                    this.getAll();
                }
            });
        }
        return;
    }
    onEdit(id) {
        if (id) {
            this.carregando = true;
            this.httpProvider.edit(id)
                .subscribe(res => {
                if (res != null && res.body.data != null) {
                    let data = res.body.data;
                    this.form = Object.assign({}, data);
                    this.autorList = data.autor;
                    this.form.Assunto_CodAs = data.assunto[0].CodAs;
                    this.carregando = false;
                }
            });
        }
    }
    onDelete(id) {
        this.idDel = id;
    }
    onSubmitDelete() {
        if (this.idDel) {
            this.carregando = true;
            this.httpProvider.delete(this.idDel).subscribe((response) => {
                if (response) {
                    this.toastService.add("Dados deletados com sucesso!", 3000, 'success');
                    this.getAll();
                }
            });
        }
    }
    get result() {
        return this.autorList.filter((item) => item.checked);
    }
}
